import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';

export default function PresentationLayer() {
  // Define a boolean variable to track whether the loader should be shown
  let isLoading = false;

  // Function to force re-render
  const forceUpdate = React.useCallback(() => {
    setUpdate({});
  }, []);
  const [, setUpdate] = React.useState({});

  // Handler function for the submit button click
  const handleSubmit = () => {
    // Set isLoading to true to show the loader
    isLoading = true;
    // Force re-render
    forceUpdate();

    // Simulate a delay of 1 second before hiding the loader
    setTimeout(() => {
      isLoading = false;
      // Force re-render
      forceUpdate();
    }, 1000);
  };

  return (
    <Box sx={{ padding: '20px' }}>
      <Box sx={{ display: 'flex', padding: '10px' }}>
        <TextField
          id="searchShop"
          label="Search for shop"
          fullWidth
        ></TextField>
        <TextField
          id="searchInventory"
          label="Select for inventory"
          fullWidth
          sx={{ marginLeft: '10px' }}
        ></TextField>
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: '30px' }}>
        <button
          style={{ padding: '10px 20px', borderRadius: '7px' }}
          onClick={handleSubmit} // Call handleSubmit function on button click
        >
          Search
        </button>
      </Box>

      {/* Conditional rendering of loader based on isLoading value */}
      {isLoading && (
        <div className="loader" style={{ marginTop: '30px', textAlign: 'center' }}>
          Loading...
        </div>
      )}

      <Box sx={{ marginTop: '30px' }}>
        <DataGrid
          rows={rows}
          columns={[
            { field: 'id', headerName: 'ID', width: 500 },
            { field: 'shop', headerName: 'Shop', width: 500 },
            { field: 'inventory', headerName: 'Inventory', width: 500 },
            { field: 'products', headerName: 'Products', width: 500 }
          ]}
          checkboxSelection
        ></DataGrid>
      </Box>
    </Box>
  );
}
