import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';

// Define a type for the row object
type Row = {
  id: number;
  shop: string;
  inventory: string;
  product: string;
};

export default function ShopsPage() {
  const [shopFilter, setShopFilter] = React.useState('');
  const [inventoryFilter, setInventoryFilter] = React.useState('');
  const [searchTerm, setSearchTerm] = React.useState('');

  // Cast the rows array to an array of Row type
  const shops = [...new Set((rows as Row[]).map(row => row.shop))];
  const inventories = [...new Set((rows as Row[]).map(row => row.inventory))];

  const filteredRows = React.useMemo(() => {
    return rows.filter(row => {
      const shopMatch = shopFilter === '' || row.shop.toLowerCase().includes(shopFilter.toLowerCase());
      const inventoryMatch = inventoryFilter === '' || row.inventory.toLowerCase().includes(inventoryFilter.toLowerCase());
      const searchMatch = searchTerm === '' || Object.values(row).some(value => value.toString().toLowerCase().includes(searchTerm.toLowerCase()));
      return shopMatch && inventoryMatch && searchMatch;
    });
  }, [shopFilter, inventoryFilter, searchTerm]);

  return (
    <Box sx={{ height: '100%', width: '100%', padding: '20px' }}>
      <TextField
        select
        label="Search for shops"
        variant="outlined"
        value={shopFilter}
        onChange={(e) => setShopFilter(e.target.value)}
        fullWidth
        sx={{ marginBottom: '10px' }}
      >
        <MenuItem value="">All Shops</MenuItem>
        {shops.map((shop, index) => (
          <MenuItem key={index} value={shop}>{shop}</MenuItem>
        ))}
      </TextField>
      <TextField
        select
        label="Search for inventory"
        variant="outlined"
        value={inventoryFilter}
        onChange={(e) => setInventoryFilter(e.target.value)}
        fullWidth
        sx={{ marginBottom: '10px' }}
      >
        <MenuItem value="">All Inventories</MenuItem>
        {inventories.map((inventory, index) => (
          <MenuItem key={index} value={inventory}>{inventory}</MenuItem>
        ))}
      </TextField>
      <TextField
        label="Search"
        variant="outlined"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        fullWidth
        sx={{ marginBottom: '20px' }}
      />
      <Box sx={{ height: 'calc(100% - 172px)', width: '100%' }}>
        <DataGrid
          rows={filteredRows}
          columns={[
            { field: 'id', headerName: 'ID', width: 90 },
            { field: 'shop', headerName: 'Shop', width: 150 },
            { field: 'inventory', headerName: 'Inventory', width: 150 },
            { field: 'product', headerName: 'Product', width: 150 },
          ]}
          checkboxSelection
        />
      </Box>
    </Box>
  );
}
