import React, { useRef } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { DataGrid, GridApi } from '@mui/x-data-grid';
import { rows } from './MockData';

export default function PresentationLayer() {
  const dataGridRef = useRef<HTMLDivElement>(null); // Change the type of ref

  const handleSearch = (event: React.ChangeEvent<HTMLInputElement>) => {
    const searchValue = event.target.value.toLowerCase();
    const results = rows.filter(row =>
      Object.values(row).some(value =>
        typeof value === 'string' && value.toLowerCase().includes(searchValue))
    );
    updateDataGrid(results);
  };

  const updateDataGrid = (filteredRows: any[]) => {
    if (dataGridRef.current) {
      // Check if ref is valid
      const gridApi = dataGridRef.current.getApi(); // Use getApi to access the DataGrid API
      gridApi.updateRows(filteredRows); // Use the API to update rows
    }
  };

  return (
    <Box sx={{ padding: '20px' }}>
      <Box sx={{ display: 'flex', padding: '10px' }}>
        <TextField
          id="searchInput"
          label="Search for shop"
          fullWidth
          onChange={handleSearch}
        ></TextField>
      </Box>
      <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: '30px' }}>
        <button style={{ padding: '10px 20px', borderRadius: '7px' }}>Search</button>
      </Box>
      <Box sx={{ marginTop: '30px' }}>
        <DataGrid
          rows={rows}
          columns={[
            { field: 'id', headerName: 'ID', width: 500 },
            { field: 'shop', headerName: 'Shop', width: 500 },
            { field: 'inventory', headerName: 'Inventory', width: 500 },
            { field: 'products', headerName: 'Products', width: 500 }
          ]}
          checkboxSelection
          className="data-grid"
          ref={dataGridRef}
        ></DataGrid>
      </Box>
    </Box>
  );
}
