import React from 'react';
import { SxProps, Theme, Typography, Box, FormControl, MenuItem, Select, InputLabel, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { shopsPageMockData } from './mockData';

export default function ShopsPage() {

  const wholeContainer : SxProps<Theme> = {
    'minHeight': '100vh',
    'padding': '3%',
  }

  const buttonContainer : SxProps<Theme> = {
    'border': '1px solid',
    'borderRadius': '8px',
    'padding': '8px',
    'marginBottom': '30px',
  }

  const container: SxProps<Theme> = {
    'minHeight': '40vh',
    'display': 'flex',
    'border': '1px solid',
    'marginTop': '10px',
    'justifyContent': 'space-between',
    'flexWrap': 'wrap',
  };

  const button1: SxProps<Theme> = {
    'display': 'flex',
    'justifyContent': 'center',
    'margin': '1%',
    'padding': '8px 16px',
    'boxSizing': 'border-box',
    'width': 'auto',
    'height': '5vh',
    'alignItems': 'center',
  };

  const button2: SxProps<Theme> = {
    'display': 'flex',
    'justifyContent': 'center',
    'margin': '1%',
    'padding': '8px 16px',
    'boxSizing': 'border-box',
    'width': 'auto',
    'height': '5vh',
    'alignItems': 'center',
  };

  const button3: SxProps<Theme> = {
    'display': 'flex',
    'justifyContent': 'center',
    'margin': '1%',
    'padding': '8px 16px',
    'boxSizing': 'border-box',
    'width': 'auto',
    'height': '5vh',
    'alignItems': 'center',
  };

  const navigation = useNavigate();

  const handleAddShopsClick = () => {
    navigation('/AddShops');
  };

  const handleAddInventoriesClick = () => {
    navigation('/AddInventories');
  };

  const handleAddProductsClick = () => {
    navigation('/AddProducts');
  };

  const generateMenuItems =()=>{
    return shopsPageMockData.shopsList.map((shop)=>(
      <MenuItem>
        <span>{shop.options[0].label}</span>
      </MenuItem>
    ))
  }

  return (
    <Box sx={wholeContainer}>
      <Box sx={buttonContainer}>
        <Button onClick={handleAddShopsClick} sx={button1} variant="contained">ADD SHOPS</Button>
        <Button onClick={handleAddInventoriesClick} sx={button2} variant="contained">ADD INVENTORIES</Button>
        <Button onClick={handleAddProductsClick} sx={button3} variant="contained">ADD PRODUCTS</Button>
      </Box>

      <Box sx={{ display: 'flex', padding: '10px' }}>
        <FormControl fullWidth>
          <InputLabel id="searchShop">SEARCH SHOP</InputLabel>
          <Select
            id="searchShop"
            label="Search for shop"
            fullWidth
            value="Search for shop"
            sx={{ marginRight: '10px' }}
          >
            {generateMenuItems()}
          </Select>
        </FormControl>
        <Button sx={{ padding: '10px 20px', borderRadius: '7px' }}>SUBMIT</Button>
      </Box>

      <Box id="loader" sx={{ display: 'none' }}>Loading...</Box>
    </Box>
  );
}
