import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';
import Axios from 'axios';

export default function PresentationLayer() {

//   const fetchingData = () =>{
//     Axios.get('http://192.168.0.105:3001/allShops')
//     .then(response => {
//       console.log('Data:', response.data);
//     })
//     .catch(error => {
//       console.error('Error:', error);
//     });
//   }

  const handleSearch = (event) => {
    const searchValue = event.target.value.toLoweCase()
  }

  return (
    <Box sx={{ padding: '20px' }}>

      <Box sx={{ display: 'flex', padding: '10px' }}>
        <TextField
          label="Search for shop"
          fullWidth
        ></TextField>
        <TextField
          label="Select for inventory"
          fullWidth
          sx={{ marginLeft: '10px' }}
        >
        </TextField>
      </Box>

      <Box sx = {{display:'flex', justifyContent:'center', marginTop:'30px'}}>
        <button onChange={handleSearch} style = {{padding:'10px 20px',borderRadius:'7px'}}>Search</button>
      </Box>

      <Box sx = {{marginTop:'30px'}}>
        <DataGrid
            rows = {rows}
            columns = {[
                {field:'id', headerName:'ID', width: 500},
                {field:'shop', headerName:'Shop', width: 500},
                {field:'inventory', headerName:'Inventory', width: 500},
                {field:'products', headerName:'Products', width:500}
            ]}
            checkboxSelection
        ></DataGrid>
      </Box>

    </Box>
  );
}
