import React from 'react';
import Box from '@mui/material/Box';
import { DataGrid, GridColDef } from '@mui/x-data-grid';
import { Button } from '@mui/material';
import { productsPageMockData } from '../MockData/ProductsPage';
import backgroundImage from './background.avif';

export default function ProductsPage() {

  // Define columns for the DataGrid
  const columns: GridColDef[] = [
    { field: 'sno', headerName: 'S.No', width: 100, align: 'center' },
    { field: 'name', headerName: 'Name', width: 200 },
    { field: 'quantity', headerName: 'Quantity', width: 150, align: 'center' },
    { field: 'price', headerName: 'Price', width: 150, align: 'center' },
  ];

  // Format product data for the DataGrid
  const formatDataForGrid = () => {
    return productsPageMockData.tables[0].products.map((product) => ({
      id: product.sno,
      sno: product.sno,
      name: product.name,
      quantity: product.quantity,
      price: product.price,
    }));
  }

  // Styles
  const wholeContainer = {
    overflow: 'auto',
    minWidth: '800px',
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
  };

  const body = {
    display: 'flex',
    flexDirection: 'column',
    padding: '10px',
    alignItems: 'center',
    marginTop: '100px',
    backgroundColor: 'red',
  };

  const searchButton = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: '20vh',
  };

  return (
    <Box sx={wholeContainer}>
      <Box sx={body}>
        {/* Render DataGrid */}
        <div style={{ height: 400, width: '100%' }}>
          <DataGrid
            rows={formatDataForGrid()}
            columns={columns}
            rowsPerPageOptions={[5, 10, 20]}
            disableSelectionOnClick
            columnBuffer={8} // Adjust columnBuffer for better alignment
          />
        </div>
      </Box>

      <Box sx={searchButton}>
        <Button variant="contained">SUBMIT</Button>
      </Box>
    </Box>
  );
}
