import { Button, SxProps, Theme, Typography, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import backgroundImage from './background.avif';
import { useState } from "react";


function AddInventories() {

    const navigation = useNavigate();
    const[showAlert,setShowAlert] = useState(false);

    const handleNavigation = () =>{
        setShowAlert(false);
        setTimeout()
        navigation('/AddProducts');
    }

    const handleAddInventory = () => {
        const container = document.querySelector('.inventory-container');
        if (container) {
            const newInput = document.createElement('div');
            newInput.innerHTML = `
                <div>
                    <label>ENTER THE INVENTORY NAME:</label>
                    <input placeholder="INVENTORY NAME" type='text' />
                </div>
            `;
            container.appendChild(newInput.firstChild as Node);
        }
    }


    const wholeConInventories: SxProps<Theme> = {
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover', 
        backgroundPosition: 'center', 
        minWidth: '800px',
    }

    const headerInventories: SxProps<Theme> = {
        display: 'flex',
        justifyContent: 'center',
        fontFamily: 'Times New Roman, Times, serif',
        fontSize: 'x-large',
    }

    const container: SxProps<Theme> = {
        display: 'flex',
        width: '600px',
        flexDirection: 'column',
        justifyContent:'center',
        flex:1,
        marginTop:-20,
    }

    const bodyInventories: SxProps<Theme> = {
        display: 'flex',
        flexDirection: 'row',
    }

    const body1: SxProps<Theme> = {
        display: 'flex',
        flex: 1,
        alignItems: 'center',
        fontFamily:'timesnewroman',
        fontSize:'18px',
        maxWidth:'300px'
    }

    const body2: SxProps<Theme> = {
        display: 'flex',
        flex: 1,
        alignItems: 'center',
    }

    const AddButton: SxProps<Theme> = {
        display:'flex',
        borderRadius:'20px',
        marginLeft:'-70px',
        height: '25px' 
    }

    const submitButton : SxProps<Theme> = {
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
        minHeight:'10vh',
    }

    return (
        <Box sx={wholeConInventories}>
            <Box sx={headerInventories}>
                <h2>HERE YOU CAN ADD THE INVENTORIES</h2>
            </Box>
            <Box sx={container}>
                <Box sx={bodyInventories}>
                    <Box sx={body1}><label>ENTER THE SHOP NAME      : </label></Box>
                    <Box sx={body2}><input placeholder="SHOP NAME" type='text'></input></Box><br /><br /><br /><br />
                </Box>
                <Box sx={bodyInventories}>
                    <Box sx={body1}>ENTER THE INVENTORY NAME :</Box>
                    <Box sx={body2}><input placeholder="INVENTORY NAME" type='text'></input></Box> 
                    <Box sx={AddButton}><Button onClick={handleAddInventory} variant="contained">ADD</Button></Box> 
                </Box>
                <Box sx = {submitButton}><Button onClick={handleNavigation} variant="contained">ADD INVENTORIES</Button></Box>
                {showAlert &&
                    <Box sx={alertStyle}>
                        <Alert>
                            SHOPS ADDED SUCCESSFULLY
                        </Alert>
                </Box>}
            </Box>
        </Box>
    )
}
export default AddInventories
