import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { DataGrid } from '@mui/x-data-grid'; // Ensure correct import

import { rows } from './MockData';

export default function ShopsPage() {
  const [shopFilter, setShopFilter] = React.useState('');
  const [inventoryFilter, setInventoryFilter] = React.useState('');

  const filteredRows = React.useMemo(() => {
    return rows.filter(row => {
      // Check if the shop name and inventory contain the filter text
      const shopMatch = row.shop.toLowerCase().includes(shopFilter.toLowerCase());
      const inventoryMatch = row.inventory.toLowerCase().includes(inventoryFilter.toLowerCase());
      // Return true if both conditions are met
      return shopMatch && inventoryMatch;
    });
  }, [shopFilter, inventoryFilter]);

  return (
    <Box sx={{ height: '100%', width: '100%', padding: '20px' }}>
      <TextField
        label="Search for shops"
        variant="outlined"
        value={shopFilter}
        onChange={(e) => setShopFilter(e.target.value)}
        sx={{ marginBottom: '10px' }}
      />
      <TextField
        label="Search for inventory"
        variant="outlined"
        value={inventoryFilter}
        onChange={(e) => setInventoryFilter(e.target.value)}
        sx={{ marginBottom: '10px' }}
      />
      <Box sx={{ height: 'calc(100% - 104px)', width: '100%' }}>
        <DataGrid
          rows={filteredRows}
          columns={[
            { field: 'id', headerName: 'ID', width: 90 },
            { field: 'shop', headerName: 'Shop', width: 150 },
            { field: 'inventory', headerName: 'Inventory', width: 150 },
            { field: 'product', headerName: 'Product', width: 150 },
          ]}
          pageSize={5}
          checkboxSelection
          disableSelectionOnClick // Corrected prop name
        />
      </Box>
    </Box>
  );
}
