import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';

// Define a type for the row object
type Row = {
  id: number;
  shop: string;
  inventory: string;
  product: string;
};

export default function ShopsPage() {
  const [filter, setFilter] = React.useState('');
  const [searchTerm, setSearchTerm] = React.useState('');

  // Cast the rows array to an array of Row type
  const shops = [...new Set((rows as Row[]).map(row => row.shop))];
  const inventories = [...new Set((rows as Row[]).map(row => row.inventory))];

  const filteredRows = React.useMemo(() => {
    return rows.filter(row => {
      const shopMatch = row.shop.toLowerCase().includes(filter.toLowerCase());
      const inventoryMatch = row.inventory.toLowerCase().includes(filter.toLowerCase());
      const searchMatch = searchTerm === '' || Object.values(row).some(value => value.toString().toLowerCase().includes(searchTerm.toLowerCase()));
      return (shopMatch || inventoryMatch) && searchMatch;
    });
  }, [filter, searchTerm]);

  return (
    <Box sx={{ height: '100%', width: '100%', padding: '20px' }}>
      <TextField
        select
        label="Search"
        variant="outlined"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        fullWidth
        sx={{ marginBottom: '20px' }}
      >
        <MenuItem value="">All</MenuItem>
        <MenuItem value={shops.join(',')}>Shops</MenuItem>
        <MenuItem value={inventories.join(',')}>Inventories</MenuItem>
      </TextField>
      <TextField
        label="Type to search"
        variant="outlined"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        fullWidth
      />
      <Box sx={{ height: 'calc(100% - 144px)', width: '100%' }}>
        <DataGrid
          rows={filteredRows}
          columns={[
            { field: 'id', headerName: 'ID', width: 90 },
            { field: 'shop', headerName: 'Shop', width: 150 },
            { field: 'inventory', headerName: 'Inventory', width: 150 },
            { field: 'product', headerName: 'Product', width: 150 },
          ]}
          checkboxSelection
        />
      </Box>
    </Box>
  );
}
