import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';

export default function PresentationLayer() {

  const handleSearch = (event: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    const inputElement = (event.target as HTMLElement).parentElement?.querySelector('input');
    if (inputElement) {
      const searchValue = inputElement.value.toLowerCase();
      const results = rows.filter(row =>
        Object.values(row).some(value =>
          typeof value === 'string' && value.toLowerCase().includes(searchValue))
      );
      updateDataGrid(results);
    }
  }

  const updateDataGrid = (filteredRows: any[]) => {
    const grid = document.querySelector('.data-grid');
    if (grid instanceof HTMLElement) {
      const dataGrid = (grid as any).__reactEventHandlers;
      dataGrid.setState({ rows: filteredRows });
    }
  };

  return (
    <Box sx={{ padding: '20px' }}>

      <Box sx={{ display: 'flex', padding: '10px' }}>
        <TextField
          label="Search for shop"
          fullWidth
        ></TextField>
        <TextField
          label="Select for inventory"
          fullWidth
          sx={{ marginLeft: '10px' }}
        >
        </TextField>
      </Box>

      <Box sx = {{display:'flex', justifyContent:'center', marginTop:'30px'}}>
        <button onClick={handleSearch} style = {{padding:'10px 20px',borderRadius:'7px'}}>Search</button>
      </Box>

      <Box sx = {{marginTop:'30px'}}>
        <DataGrid
            rows = {rows}
            columns = {[
                {field:'id', headerName:'ID', width: 500},
                {field:'shop', headerName:'Shop', width: 500},
                {field:'inventory', headerName:'Inventory', width: 500},
                {field:'products', headerName:'Products', width:500}
            ]}
            checkboxSelection
        ></DataGrid>
      </Box>

    </Box>
  );
}
