import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';

export default function PresentationLayer() {
  const handleSearch = () => {
    const searchValue = (document.getElementById('search-input') as HTMLInputElement).value.toLowerCase();
    const results = rows.filter(row =>
        Object.values(row).some(value =>
            typeof value === 'string' && value.toLowerCase().includes(searchValue)));
    console.log(results);
  };

  return (
    <Box sx={{ padding: '20px' }}>

      <Box sx={{ display: 'flex', padding: '10px' }}>
        <TextField
          id="search-input"
          label="Search for shop"
          fullWidth
        ></TextField>
        <TextField
          label="Select for inventory"
          fullWidth
          sx={{ marginLeft: '10px' }}
        >
        </TextField>
      </Box>

      <Box sx = {{display:'flex', justifyContent:'center', marginTop:'30px'}}>
        <button onClick={handleSearch} style={{ padding: '10px 20px', borderRadius: '7px' }}>Search</button>
      </Box>

      <Box sx = {{marginTop:'30px'}}>
        <DataGrid
            rows={rows}
            columns={[
                { field:'id', headerName:'ID', width: 500 },
                { field:'shop', headerName:'Shop', width: 500 },
                { field:'inventory', headerName:'Inventory', width: 500 },
                { field:'products', headerName:'Products', width:500 }
            ]}
            checkboxSelection
        />
      </Box>

    </Box>
  );
}
