import React, { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';

export default function PresentationLayer() {

  const handleSearch = () => {

    setTimeout(() => {
      const shopSearch = (document.getElementById('searchShop') as HTMLInputElement).value.toLowerCase();
      const shopResults = rows.filter(row =>
        Object.values(row).some(value =>
          typeof value === 'string' && value.toLowerCase().includes(shopSearch)
        )
      );

      const inventorySearch = (document.getElementById('searchInventory') as HTMLInputElement).value.toLowerCase();
      const inventoryResults = rows.filter(row =>
        Object.values(row).some(value =>
          typeof value === 'string' && value.toLowerCase().includes(inventorySearch)
        )
      );

      if (shopResults.length > 0 && inventoryResults.length > 0) {
        console.log('Successful');
      } else {
        console.log('Not found');
      }

    });
  }

  return (
    <Box sx={{ padding: '20px' }}>
      <Box sx={{ display: 'flex', padding: '10px' }}>
        <TextField
          id="searchShop"
          label="Search for shop"
          fullWidth
        ></TextField>
        <TextField
          id="searchInventory"
          label="Select for inventory"
          fullWidth
          sx={{ marginLeft: '10px' }}
        ></TextField>
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: '30px' }}>
        <button style={{ padding: '10px 20px', borderRadius: '7px' }}>search
        </button>
      </Box>


      <Box sx={{ marginTop: '30px' }}>
        <DataGrid
          rows={rows}
          columns={[
            { field: 'id', headerName: 'ID', width: 500 },
            { field: 'shop', headerName: 'Shop', width: 500 },
            { field: 'inventory', headerName: 'Inventory', width: 500 },
            { field: 'products', headerName: 'Products', width: 500 }
          ]}
          checkboxSelection
        ></DataGrid>
      </Box>
    </Box>
  );
}
