import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';

export default function PresentationLayer() {
  const [shopFilter, setShopFilter] = React.useState('');
  const [inventoryFilter, setInventoryFilter] = React.useState('');

  const shops = ['Shop A', 'Shop B'];
  const inventories = ['Grocery', 'Electronics'];

  return (
    <Box sx={{ padding: '20px' }}>
      <TextField
        select
        label="Select Shop"
        value={shopFilter}
        onChange={(e) => setShopFilter(e.target.value)}
        fullWidth
        sx={{ marginBottom: '10px' }}
      >
        {shops.map((shop, index) => (
          <MenuItem key={index} value={shop}>{shop}</MenuItem>
        ))}
      </TextField>
      <TextField
        select
        label="Select Inventory"
        value={inventoryFilter}
        onChange={(e) => setInventoryFilter(e.target.value)}
        fullWidth
        sx={{ marginBottom: '10px' }}
      >
        {inventories.map((inventory, index) => (
          <MenuItem key={index} value={inventory}>{inventory}</MenuItem>
        ))}
      </TextField>
      <Box sx={{ display: 'flex', justifyContent: 'center' }}>
        <button style={{ padding: '10px 20px', fontSize: '16px', borderRadius: '5px', backgroundColor: '#3f51b5', color: '#fff', border: 'none' }}>Search</button>
      </Box>
    </Box>
  );
}
