import { Button, SxProps, Theme, Typography, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import backgroundImage from './background.avif';
import { useState } from "react";
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';

function AddInventories() {
    const navigation = useNavigate();
    const [showAlert, setShowAlert] = useState(false);
    const [inventoryCount, setInventoryCount] = useState(1); 

    const handleNavigation = () => {
        setShowAlert(true);
        setTimeout(() => {
            setShowAlert(false);
            navigation('/AddProducts');
        }, 2000);
    }

    const handleAddInventory = () => {
        setInventoryCount(prevCount => prevCount + 1); 
    }

    const renderInventoryInputs = () => {
        const inputs = [];
        for (let i = 0; i < inventoryCount; i++) {
            inputs.push(
                <Box key={i} sx={{ display: 'flex', flexDirection: 'row', marginTop: '10px', fontFamily:'timesNewRoman' , fontSize:'25px' }}>
                    <Box sx={{ flex: 1 }}>
                        <label>ENTER THE INVENTORY NAME:</label>
                    </Box>
                    <Box sx={{ flex: 1 }}>
                        <input style={{ minWidth: '300px', minHeight: '27px' }} placeholder="INVENTORY NAME" type='text'></input>
                    </Box>
                </Box>
            );
        }
        return inputs;
    }

    const wholeConInventories: SxProps<Theme> = {
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover', 
        backgroundPosition: 'center', 
        minWidth: '800px',
    }

    const headerInventories: SxProps<Theme> = {
        display: 'flex',
        justifyContent: 'center',
        fontFamily: 'Times New Roman, Times, serif',
        fontSize: 'x-large',
    }

    const container: SxProps<Theme> = {
        display: 'flex',
        width: '800px',
        flexDirection: 'column',
        justifyContent:'center',
        flex:1,
        marginTop:-20,
    }

    const AddButton: SxProps<Theme> = {
        display:'flex',
        borderRadius:'20px',
        height: '25px', 
        justifyContent:'center',
        marginTop:'20px'
    }

    const submitButton : SxProps<Theme> = {
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
        minHeight:'10vh',
    }

    const alertStyle : SxProps<Theme> = {
        position: 'fixed',
        top: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        zIndex: 9999,
        maxWidth: '90%',
        width: '300px',
    }

    return (
        <Box sx={wholeConInventories}>
            <Box sx={headerInventories}>
                <h2>HERE YOU CAN ADD THE INVENTORIES</h2>
            </Box>
            <Box sx={container}>
                <Box sx={{ display: 'flex', flexDirection: 'row'}}>
                    <Box sx={{ flex: 1 , fontFamily:'timesNewRoman' }}>
                        <label>ENTER THE SHOP NAME:</label>
                    </Box>
                    <Box sx={{ flex: 1  }}>
                        <input style={{ minWidth: '300px', minHeight: '27px' }} placeholder="SHOP NAME" type='text'></input>
                    </Box>
                </Box>
                {renderInventoryInputs()}
                <Box sx={AddButton}><Button onClick={handleAddInventory} variant="contained">ADD</Button></Box> 
                <Box sx={submitButton}><Button onClick={handleNavigation} variant="contained">ADD INVENTORIES</Button></Box>
                {showAlert &&
                    <Box sx={alertStyle}>
                        <Alert>
                            INVENTORY ADDED SUCCESSFULLY
                        </Alert>
                </Box>}
            </Box>
        </Box>
    )
}

export default AddInventories;
