import React from 'react';
import Box from '@mui/material/Box';
import { DataGrid } from '@mui/x-data-grid';
import { rows as shopRows } from './MockDataDynamic';
import { useNavigate } from 'react-router-dom';
import { FormControl, MenuItem, Select, InputLabel, SxProps, Theme } from '@mui/material';
import { Button } from '@mui/material';
import { shopsPageMockData } from './mockData';

export default function ShopsPage() {


  const wholeContainer : SxProps<Theme> = {
    'min-height': '100vh',
    'padding': '3%',
  }
  const boxContainer : SxProps<Theme> = {
    'height': '40vh', 
    'max-height': '70vh', 
    'overflow-y': 'auto', 
  }
  const buttonContainer : SxProps<Theme> = {
    'border': '1px solid',
    'border-radius': '8px',
    'padding': '8px',
    'margin-bottom': '30px',
  }
  const addShopsButton: SxProps<Theme> = {
    '&.addShopsBut.MuiButton-contained': {
      'paddingRight': '10px',
      'marginRight': '10px',
    }
  };
  const container: SxProps<Theme> = {
    'min-height': '40vh',
    'display': 'flex',
    'border': '1px',
    'marginTop': '10px',
    'justify-content': 'space-between',
    'flex-wrap': 'wrap',
  };

  const button1: SxProps<Theme> = {
    'display': 'flex',
    'justify-content': 'center',
    'margin': '1%',
    'padding': '8px 16px',
    'box-sizing': 'border-box',
    'width': 'auto',
    'height': '5vh',
    'align-items': 'center',
  };

  const button2: SxProps<Theme> = {
    'display': 'flex',
    'justify-content': 'center',
    'margin': '1%',
    'padding': '8px 16px',
    'box-sizing': 'border-box',
    'width': 'auto',
    'height': '5vh',
    'align-items': 'center',

  };

  const button3: SxProps<Theme> = {
    'display': 'flex',
    'justify-content': 'center',
    'margin': '1%',
    'padding': '8px 16px',
    'box-sizing': 'border-box',
    'width': 'auto',
    'height': '5vh',
    'align-items': 'center',
  };

  const button : SxProps<Theme> = {
    'padding': '10px 20px', 
    'borderRadius': '7px',
  }




  const navigation = useNavigate();

  const handleAddShopsClick = () => {
    navigation('/AddShops');
  };

  const handleAddInventoriesClick = () => {
    navigation('/AddInventories');
  };

  const handleAddProductsClick = () => {
    navigation('/AddProducts');
  };

  const showLoaderForOneSecond = () => {
    const loader = document.getElementById('loader');
    if (loader) {
      loader.style.display = 'block';
      setTimeout(() => {
        loader.style.display = 'none';
      }, 1000);
    }
  };

  const generateMenuItems =()=>{
    return shopsPageMockData.shopsList.map((shop)=>(
      <MenuItem>
        <span>{shop.options[0].label}</span>
      </MenuItem>
    ))
  }

  return (
    <Box sx = {wholeContainer}>
      <Box sx = {buttonContainer}>
        <Button onClick={handleAddShopsClick} variant="contained">
          ADD SHOPS
        </Button>
        <Button onClick={handleAddInventoriesClick} variant="contained">
          ADD INVENTORIES
        </Button>
        <Button onClick={handleAddProductsClick} variant="contained">
          ADD PRODUCTS
        </Button>
      </Box>

      <Box sx={{ display: 'flex', padding: '10px' }}>
      <FormControl fullWidth>
      <InputLabel id="searchShop">SEARCH SHOP</InputLabel>
        <Select
          id="searchShop"
          label="Search for shop"
          fullWidth
          value="Search for shop"
          sx={{ marginRight: '10px' }}
        >
          {generateMenuItems()}
        </Select>
      </FormControl>

      <Box sx = {button}><button>SUBMIT</button></Box>
      </Box>

      <Box id="loader" style={{ display: 'none' }}>Loading...</Box>
    </Box>
  );
}
