import React from 'react';
import { Box, Button, FormControl, InputLabel, MenuItem, Select, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { shopsPageMockData } from './mockData';

export default function ShopsPage() {
  const navigate = useNavigate();



  const generateMenuItems = () => {
    return shopsPageMockData.shopsList.map((shop) => (
      <MenuItem>
        {shop.options[0].label}
      </MenuItem>
    ));
  };

  const wholeContainer = {
    minHeight: '100vh',
    padding: '3%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const buttonContainer = {
    display: 'flex',
    gap: '10px',
    marginBottom: '30px',
  };

  const container = {
    display: 'flex',
    gap: '10px',
    alignItems: 'center',
  };

  const loader = {
    display: 'none',
  };

  return (
    <Box sx={wholeContainer}>
      <Box sx={buttonContainer}>
        <Button variant="contained" className="addShopsBut">
          ADD SHOPS
        </Button>
      </Box>

      <Box sx={container}>
        <FormControl fullWidth>
          <InputLabel id="searchShop">SEARCH SHOP</InputLabel>
          <Select
            id="searchShop"
            label="Search for shop"
            fullWidth
            value="Search for shop"
            sx={{ marginRight: '10px' }}
          >
            {generateMenuItems()}
          </Select>
        </FormControl>

        <Button style={{ padding: '10px 20px', borderRadius: '7px' }}>SUBMIT</Button>
      </Box>

      <Typography id="loader" sx={loader}>Loading...</Typography>
    </Box>
  );
}
