import React from 'react';
import { Box, Button, FormControl, InputLabel, MenuItem, Select, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { shopsPageMockData } from './mockData';

export default function ShopsPage() {
  const navigate = useNavigate();



  const generateMenuItems = () => {
    return shopsPageMockData.shopsList.map((shop) => (
      <MenuItem>
        {shop.options[0].label}
      </MenuItem>
    ));
  };

  return (
    <Box sx={{ padding: '3%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Box sx={{ display: 'flex', gap: '10px', marginBottom: '30px' }}>
        <Button onClick={() => handleAddButtonClick('/AddShops')} variant="contained" className="addShopsBut">
          ADD SHOPS
        </Button>
        <Button onClick={() => handleAddButtonClick('/AddInventories')} variant="contained" className="addShopsBut">
          ADD INVENTORIES
        </Button>
        <Button onClick={() => handleAddButtonClick('/AddProducts')} variant="contained">
          ADD PRODUCTS
        </Button>
      </Box>

      <Box sx={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
        <FormControl fullWidth>
          <InputLabel id="searchShop">SEARCH SHOP</InputLabel>
          <Select
            id="searchShop"
            label="Search for shop"
            fullWidth
            value="Search for shop"
            sx={{ marginRight: '10px' }}
          >
            {generateMenuItems()}
          </Select>
        </FormControl>

        <Button style={{ padding: '10px 20px', borderRadius: '7px' }}>SUBMIT</Button>
      </Box>

      <Typography id="loader" sx={{ display: 'none' }}>Loading...</Typography>
    </Box>
  );
}
