import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { DataGrid } from '@mui/x-data-grid';
import { CircularProgress } from '@mui/material'; // Import CircularProgress for loader
import { rows } from './MockData';
import Axios from 'axios';

type Row = {
  id: number;
  shop: string;
  inventory: string;
  product: string;
};

export default function ShopsPage() {

  const postShops = async ()

  const [searchText, setSearchText] = React.useState('');
  const [shopFilter, setShopFilter] = React.useState('');
  const [inventoryFilter, setInventoryFilter] = React.useState('');
  const [loading, setLoading] = React.useState(false); // State for loader

  const shops = [...new Set((rows as Row[]).map(row => row.shop))];
  const inventories = [...new Set((rows as Row[]).map(row => row.inventory))];

  const handleSearch = (value: string) => {
    setSearchText(value);
    setLoading(true); 
    setTimeout(() => {
      setLoading(false); 
    }, 1000);
  };

  const filteredRows = React.useMemo(() => {
    return rows.filter(row => {
      const shopMatch = shopFilter === '' || row.shop.toLowerCase().includes(shopFilter.toLowerCase());
      const inventoryMatch = inventoryFilter === '' || row.inventory.toLowerCase().includes(inventoryFilter.toLowerCase());
      const searchMatch = searchText === '' || Object.values(row).some(value => value.toString().toLowerCase().includes(searchText.toLowerCase()));
      return shopMatch && inventoryMatch && searchMatch;
    });
  }, [shopFilter, inventoryFilter, searchText]);

  return (
    <Box sx={{ height: '100%', width: '98%', padding: '20px' }}>
      <TextField
        label="Search"
        value={searchText}
        onChange={(e) => handleSearch(e.target.value)}
        sx={{ marginBottom: '10px' }}
      />
      <Box sx={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
        <TextField
          select
          label="Search for shops"
          value={shopFilter}
          onChange={(e) => setShopFilter(e.target.value)}
          sx={{ width: '48%' }}
        >
          {shops.map((shop, index) => (
            <MenuItem key={index} value={shop}>{shop}</MenuItem>
          ))}
        </TextField>
        <TextField
          select
          label="Search for inventory"
          value={inventoryFilter}
          onChange={(e) => setInventoryFilter(e.target.value)}
          sx={{ width: '48%' }}
        >
          {inventories.map((inventory, index) => (
            <MenuItem key={index} value={inventory}>{inventory}</MenuItem>
          ))}
        </TextField>
      </Box>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 'calc(100% - 104px)' }}>
          <CircularProgress /> 
        </Box>
      ) : (
        filteredRows.length === 0 ? (
          <p>Data not found</p>
        ) : (
          <Box sx={{ height: 'calc(100% - 104px)', width: '100%' }}>
            <DataGrid
              rows={filteredRows}
              columns={[
                { field: 'id', headerName: 'ID', width: 90 },
                { field: 'shop', headerName: 'Shop', width: 150 },
                { field: 'inventory', headerName: 'Inventory', width: 150 },
                { field: 'products', headerName: 'Products', width: 100 }
              ]}
              checkboxSelection
            />
          </Box>
        )
      )}
    </Box>
  );
}
