import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { DataGrid } from '@mui/x-data-grid';
import { rows } from './MockData';

class PresentationLayer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false
    };
  }

  handleSubmit = () => {
    // Set isLoading to true to show the loader
    this.setState({ isLoading: true });

    // Simulate a delay of 1 second before hiding the loader
    setTimeout(() => {
      this.setState({ isLoading: false });
    }, 1000);
  };

  render() {
    const { isLoading } = this.state;

    return (
      <Box sx={{ padding: '20px' }}>
        <Box sx={{ display: 'flex', padding: '10px' }}>
          <TextField
            id="searchShop"
            label="Search for shop"
            fullWidth
          ></TextField>
          <TextField
            id="searchInventory"
            label="Select for inventory"
            fullWidth
            sx={{ marginLeft: '10px' }}
          ></TextField>
        </Box>

        <Box sx={{ display: 'flex', justifyContent: 'center', marginTop: '30px' }}>
          <button
            style={{ padding: '10px 20px', borderRadius: '7px' }}
            onClick={this.handleSubmit} // Call handleSubmit function on button click
          >
            Search
          </button>
        </Box>

        {/* Conditional rendering of loader based on isLoading value */}
        {isLoading && (
          <div className="loader" style={{ marginTop: '30px', textAlign: 'center' }}>
            Loading...
          </div>
        )}

        <Box sx={{ marginTop: '30px' }}>
          <DataGrid
            rows={rows}
            columns={[
              { field: 'id', headerName: 'ID', width: 500 },
              { field: 'shop', headerName: 'Shop', width: 500 },
              { field: 'inventory', headerName: 'Inventory', width: 500 },
              { field: 'products', headerName: 'Products', width: 500 }
            ]}
            checkboxSelection
          ></DataGrid>
        </Box>
      </Box>
    );
  }
}

export default PresentationLayer;
