import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { DataGrid } from '@mui/x-data-grid';
import Axios from 'axios'; 

export default function PresentationLayer() {
  const rows = [
    { id: 1, shop: 'Shop A', inventory: 'Grocery', products: 'Product 1' },
    { id: 2, shop: 'Shop A', inventory: 'Electronics', products: 'Product 2' },
    { id: 3, shop: 'Shop B', inventory: 'Grocery', products: 'Product 3' },
    { id: 4, shop: 'Shop B', inventory: 'Electronics', products: 'Product 4' }
  ];

  const isLoading = () => {
    Axios.get('http://192.168.0.105:3001/allShops')
      .then(response => {
        console.log('Data:', response.data);
      })
      .catch(error => {
        console.error('Error:', error);
      });
  };

  return (
    <Box sx={{ padding: '20px' }}>  
      <Box sx={{ display: 'flex', marginBottom: '10px' }}>
        <TextField
          select
          label="Select Shop"
          fullWidth
          sx={{ width: '50%', marginRight: '10px' }}
        >
          <MenuItem value="">None</MenuItem>
          {rows.map(row => (
            <MenuItem key={row.id} value={row.shop}>{row.shop}</MenuItem>
          ))}
        </TextField>
        <TextField
          select
          label="Select Inventory"
          fullWidth
          sx={{ width: '50%' }}
        >
          <MenuItem value="">None</MenuItem>
          {rows.map(row => (
            <MenuItem key={row.id} value={row.inventory}>{row.inventory}</MenuItem>
          ))}
        </TextField>
      </Box>
      <Box sx={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
        <button onClick={isLoading} style={{ padding: '10px 20px', fontSize: '16px', borderRadius: '5px', backgroundColor: '#3f51b5', color: '#fff', border: 'none' }}>Search</button>
      </Box>
      <div style={{ height: 400, width: '100%' }}>
        <DataGrid
          rows={rows}
          columns={[
            { field: 'id', headerName: 'ID', width: 90 },
            { field: 'shop', headerName: 'Shop', width: 150 },
            { field: 'inventory', headerName: 'Inventory', width: 150 },
            { field: 'products', headerName: 'Products', width: 150 }
          ]}
          checkboxSelection
        />
      </div>
    </Box>
  );
}
