import React from 'react';
import Box from '@mui/material/Box';
import { DataGrid } from '@mui/x-data-grid';
import { rows as shopRows } from './MockDataDynamic';
import '../Css/ShopsPage.css';
import { useNavigate } from 'react-router-dom';
import { MenuItem, Select, InputLabel, FormControl } from '@mui/material'; // Import InputLabel and FormControl
import { Button } from '@mui/material';
import { shopsPageMockData } from './mockData';

export default function ShopsPage() {
  const navigation = useNavigate();

  const handleAddShopsClick = () => {
    navigation('/AddShops');
  };

  const handleAddInventoriesClick = () => {
    navigation('/AddInventories');
  };

  const handleAddProductsClick = () => {
    navigation('/AddProducts');
  };

  const showLoaderForOneSecond = () => {
    const loader = document.getElementById('loader');
    if (loader) {
      loader.style.display = 'block';
      setTimeout(() => {
        loader.style.display = 'none';
      }, 1000);
    }
  };

  const generateMenuItems =()=>{
    return shopsPageMockData.shopsList.map((shop)=>(
      <MenuItem>
        <span>{shop.options[0].label}</span>
      </MenuItem>
    ))
  }

  return (
    <div className='wholeContainer'>
      <div className="buttonContainer">
        <Button onClick={handleAddShopsClick} className='addShopsBut' variant="contained">
          ADD SHOPS
        </Button>
        <Button onClick={handleAddInventoriesClick} className='addShopsBut' variant="contained">
          ADD INVENTORIES
        </Button>
        <Button onClick={handleAddProductsClick} variant="contained">
          ADD PRODUCTS
        </Button>
      </div>

      <Box sx={{ display: 'flex', padding: '10px' }}>
  <InputLabel id="searchShopLabel">Search for shop</InputLabel>
  <Select
    labelId="searchShopLabel"
    id="searchShop"
    fullWidth
    sx={{ marginRight: '10px' }}
    value=""  // Set the value to empty to clear any pre-selected option
    onChange={(event) => { /* Add your onChange handler here */ }}
    renderValue={(value) => ( // Render the selected value or placeholder
      <em>Select a shop</em>
    )}
  >
    {generateMenuItems()}
  </Select>

  <button style={{ padding: '10px 20px', borderRadius: '7px' }}>SUBMIT</button>
</Box>


      <div id="loader" style={{ display: 'none' }}>Loading...</div>
    </div>
  );
}
