import { Button, SxProps, Theme, Typography, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import backgroundImage from './background.avif';
import { useState } from "react";
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';

function AddShops() {
    const navigation = useNavigate();
    const [showAlert, setShowAlert] = useState(false);

    const handleNavigation = () =>{
        setShowAlert(true);
        setTimeout(() => {
            setShowAlert(false);
            navigation('/AddInventories');
        }, 2000); // Hide alert and navigate after 2 seconds
    }

    const wholeConShops: SxProps<Theme> = {
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover', 
        backgroundPosition: 'center', 
        minWidth: '800px',
    }

    const headerShops: SxProps<Theme> = {
        display: 'flex',
        justifyContent: 'center',
        fontFamily: 'Times New Roman, Times, serif',
        fontSize: 'x-large',
        marginLeft:'-90px'
    }

    const container: SxProps<Theme> = {
        display: 'flex',
        width: '600px',
        flex:1,
        flexDirection: 'column',
        justifyContent:'center',
        marginTop:-20,
    }

    const bodyShops: SxProps<Theme> = {
        display: 'flex',
        flexDirection: 'row',
    }

    const body1: SxProps<Theme> = {
        display: 'flex',
        flex: 1,
        alignItems: 'center',
        fontFamily:'timesnewroman',
        fontSize:'18px',
    }

    const body2: SxProps<Theme> = {
        display: 'flex',
        flex: 1,
        alignItems: 'center',
    }

    const shopDetails: SxProps<Theme> = {
        marginTop:'20px',
        marginLeft:'20px',
        fontFamily:'TimesNewRoman',
        display:'flex',
        flexDirection:'column',
    }

    const shopDetailsPart : SxProps<Theme> = {
        display: 'flex',
        flexDirection: 'row',
    }

    const shopDetails1: SxProps<Theme> = {
        display:'flex',
        flex: 1,
        alignItems:'center',
        fontSize: 'large',
    }

    const shopDetails2: SxProps<Theme> = {
        display:'flex',
        flex:1,
        alignItems:'center',
    }

    const submitButton : SxProps<Theme> = {
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
        minHeight:'10vh'
    }

    return (
        <Box sx={wholeConShops}>
            <Box sx={headerShops}>
                <h2>HERE YOU CAN ADD THE INVENTORIES</h2>
            </Box>
            <Box sx={container}>
                <Box sx={bodyShops}>
                    <Box sx={body1}><label>ENTER THE SHOP NAME      : </label></Box>
                    <Box sx={body2}><input placeholder="SHOP NAME" type='text'></input></Box><br /><br /><br /><br />
                </Box>
                <Box sx={bodyShops}>
                    <Box sx={body1}>ENTER THE SHOPLOCATION </Box>
                </Box>
                    <Box sx = {shopDetails}>
                        <Box sx = {shopDetailsPart}>
                            <Box sx={shopDetails1}><label>ENTER THE SHOP'S COUNTRY :</label></Box>
                            <Box sx={shopDetails2}><input placeholder="COUNTRY" type='text'></input></Box><br /><br />
                        </Box >
                        <Box sx = {shopDetailsPart}>
                            <Box sx={shopDetails1}><label>ENTER THE SHOP'S STATE :</label></Box>
                            <Box sx={shopDetails2}><input placeholder="STATE" type='text'></input></Box><br /><br />
                        </Box>
                        <Box sx = {shopDetailsPart}>
                            <Box sx={shopDetails1}><label>ENTER THE SHOP'S CITY</label></Box>
                            <Box sx={shopDetails2}><input placeholder="CITY" type='text'></input></Box><br /><br />
                        </Box>
                    </Box>
                <Box sx = {submitButton}>
                    <Button onClick={handleNavigation} variant="contained">ADD SHOPS</Button>
                    {showAlert && (
                        <Alert icon={<CheckIcon fontSize="inherit" />} severity="success">
                            Here is a gentle confirmation that your action was successful.
                        </Alert>
                    )}
                </Box>
            </Box>
        </Box>
    )
}
export default AddShops;
