import React from 'react';
import Box from '@mui/material/Box';
import { DataGrid } from '@mui/x-data-grid';
import { rows as shopRows } from './MockDataDynamic';
import '../Css/ShopsPage.css';
import { useNavigate } from 'react-router-dom';
import { MenuItem, Select } from '@mui/material';
import { Button } from '@mui/material';

export default function ShopsPage() {
  const navigation = useNavigate();

  const handleAddShopsClick = () => {
    navigation('/AddShops');
  };

  const handleAddInventoriesClick = () => {
    navigation('/AddInventories');
  };

  const handleAddProductsClick = () => {
    navigation('/AddProducts');
  };

  const showLoaderForOneSecond = () => {
    const loader = document.getElementById('loader');
    if (loader) {
      loader.style.display = 'block';
      setTimeout(() => {
        loader.style.display = 'none';
      }, 1000);
    }
  };

  const generateMenuItems =()=>{
    return shopsPageMo
  }

  return (
    <div className='wholeContainer'>
      <div className="buttonContainer">
        <Button onClick={handleAddShopsClick} className='addShopsBut' variant="contained">
          ADD SHOPS
        </Button>
        <Button onClick={handleAddInventoriesClick} className='addShopsBut' variant="contained">
          ADD INVENTORIES
        </Button>
        <Button onClick={handleAddProductsClick} variant="contained">
          ADD PRODUCTS
        </Button>
      </div>

      <Box sx={{ display: 'flex', padding: '10px' }}>
        <Select
          id="searchShop"
          label="Search for shop"
          fullWidth
          sx={{ marginRight: '10px' }}
        >
          {generateMenuItems()}
        </Select>

        <Select
          id="searchInventory"
          label="Select for inventory"
          fullWidth
          sx={{ marginRight: '10px' }}
        >
          {generateMenuItems()}
        </Select>
        <button style={{ padding: '10px 20px', borderRadius: '7px' }}>SEARCH</button>
      </Box>

      <div id="loader" style={{ display: 'none' }}>Loading...</div>

      <div className='boxContainer'>
        <Box sx={{ marginTop: '30px', overflowX: 'auto' }}>
          <DataGrid
            rows={shopRows}
            columns={[
              { field: 'id', headerName: 'ID', flex: 1 },
              { field: 'shop', headerName: 'Shop', flex: 1 },
            ]}
            checkboxSelection
          />
        </Box>
      </div>

      <div className='responseData'>

      </div>
    </div>
  );
}
