import { Button, SxProps, Theme, Typography, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import backgroundImage from './background.avif';
import { useState } from "react";


function AddShops() {

    const[isHovered, setIsHovered] = useState(false);

    const wholeConShops: SxProps<Theme> = {
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover', 
        backgroundPosition: 'center', 
        minWidth: '800px',
    }

    const headerShops: SxProps<Theme> = {
        display: 'flex',
        justifyContent: 'center',
        fontFamily: 'Times New Roman, Times, serif',
        fontSize: 'x-large',
    }

    const bodyShops: SxProps<Theme> = {
        display: 'flex',
        flexDirection: 'row'
    }

    const container: SxProps<Theme> = {
        display: 'flex',
        flex: 1,
        width: '500px',
        flexDirection: 'column',
        marginTop:'180px'
    }

    const body1: SxProps<Theme> = {
        display: 'flex',
        flex: 1,
        alignItems: 'center',
        fontFamily:'timesnewroman',
        fontSize:'18px'
    }

    const body2: SxProps<Theme> = {
        display: 'flex',
        flex: 1,
        alignItems: 'center'
    }

    const submitButton : SxProps<Theme> = {
        display:'flex',
        justifyContent:'center',
        alignItems:'center',
        minHeight:'10vh'
    }



    return (
        <Box sx={wholeConShops}>
            <Box sx={headerShops}>
                <h2>HERE YOU CAN ADD THE SHOPS</h2>
            </Box>
            <Box sx={container}>
                <Box sx={bodyShops}>
                    <Box sx={body1}><label>ENTER THE SHOP NAME      : </label></Box>
                    <Box sx={body2}><input type='text'></input></Box><br /><br /><br /><br />
                </Box>
                <Box sx={bodyShops}>
                    <Box sx={body1}><label>ENTER THE SHOPLOCATION : </label></Box>
                    <Box sx={body2}><input type='text'></input></Box><br /><br />
                </Box>
                <Box onClick = {()=> setIsHovered(true)}  onMouseLeave = {()=> setIsHovered(false)}></Box>
                {isHovered && (
                    <Box>
                        <label>ENTER THE SHOP'S COUNTRY</label>
                        <input type='text'></input><br /><br />
                        <label>ENTER THE SHOP'S STATE</label>
                        <input type='text'></input><br /><br />

                    </Box>
                )}
                <Box sx = {submitButton}><Button variant="contained">ADD SHOPS</Button></Box>
            </Box>

        </Box>
    )
}
export default AddShops
