import { Button, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import backgroundImage from './background.avif';

function AddInventories() {
    const navigation = useNavigate();

    const handleNavigation = () => {
        navigation('/AddProducts');
    }

    const handleAddInventory = () => {
        console.log("Adding inventory...");
        const container = document.querySelector('.inventory-container');
        console.log("Container:", container);
        if (container) {
            const newInput = document.createElement('div');
            newInput.innerHTML = `
                <div>
                    <label>ENTER THE INVENTORY NAME:</label>
                    <input placeholder="INVENTORY NAME" type='text' />
                </div>
            `;
            if (newInput.firstChild) {
                container.appendChild(newInput.firstChild);
                console.log("Inventory added.");
            } else {
                console.error("Failed to add inventory: No child nodes found in new input.");
            }
        } else {
            console.error("Container not found.");
        }
    }

    return (
        <Box>
            <div className="inventory-container"></div>
            <Button onClick={handleAddInventory} variant="contained">ADD</Button>
            <Button onClick={handleNavigation} variant="contained">ADD INVENTORIES</Button>
        </Box>
    );
}

export default AddInventories;
